import React, { useState } from 'react'
import TodoForm from './components/TodoForm'
import TodoList from './components/TodoList'
function App() {
    const [todos, setTodos] = useState([])

    const addTodos = (todo) => {
        setTodos([...todos, todo])
    }
    console.log(todos)
    return (
        <div>
            <TodoForm addTodos={addTodos} />
            <TodoList />
        </div>
    )
}

export default App
