import React from 'react'

function Todo({ todo, removeTodo, todoComplete }) {
    const handleCheckBox = () => {
        todoComplete(todo.id)
    }
    return (
        <div
            style={{
                display: 'flex',
                alignItems: 'center',
            }}
        >
            <button onClick={() => removeTodo(todo.id)}>X</button>
            <p
                style={{
                    textDecoration: todo.complete ? 'underline' : null,
                }}
            >
                {todo.task}
            </p>
            <input onClick={() => handleCheckBox} type="checkbox" />
        </div>
    )
}

export default Todo
