import React, { useState } from 'react'

function TodoForm({ addTodos }) {
    const [todo, setTodo] = useState([])
    const handleChange = (e) => {
        setTodo(e.target.value)
    }
    console.log(todo)
    return (
        <div
            style={{
                display: 'flex',
            }}
        >
            <input
                onChange={handleChange}
                value={todo}
                placeholder="add todos"
            />
            <button>add</button>
        </div>
    )
}

export default TodoForm
