import React, { useState, useEffect } from 'react'
import TodoForm from './components/TodoForm'
import TodoList from './components/TodoList'
function App() {
    const [todos, setTodos] = useState([])
    const addTodo = (todo) => {
        setTodos([...todos, todo])
    }
    const removeTodo = (id) => {
        setTodos(todos.filter((todo) => todo.id !== id))
    }
    const resolvedTodo = (id) => {
        setTodos(
            todos.map((todo) => {
                if (todo.id === id) {
                    return {
                        ...todo,
                        completed: !todo.completed,
                    }
                }
                return todo
            })
        )
    }
    useEffect(() => {
        const data = localStorage.setItem('todo-data')
        JSON.parse(data)
    })
    useEffect(() => {
        localStorage.getItem('todo-data', JSON.stringify(todos))
    }, [todos])
    return (
        <div>
            <h1>Add Your Task</h1>
            <TodoForm addTodo={addTodo} />
            <TodoList
                resolvedTodo={resolvedTodo}
                removeTodo={removeTodo}
                todos={todos}
            />
        </div>
    )
}

export default App
