import React, { useState } from 'react'

function TodoForm({ addTodos }) {
    const [todo, setTodo] = useState({
        id: '',
        task: '',
        completed: false,
    })
    const handleInput = (e) => {
        setTodo({ ...todo, task: e.target.value })
    }
    console.log(todo)
    return (
        <div
            style={{
                display: 'flex',
            }}
        >
            <input
                onChange={handleInput}
                value={todo}
                placeholder="add todos"
            />
            <button>add</button>
        </div>
    )
}

export default TodoForm
