import React from 'react'

const Todo = ({ todo, handleDelete, resolved }) => {
    return (
        <div
            style={{
                display: 'flex',
            }}
        >
            <input type="checkbox" onClick={() => resolved(todo.id)} />
            <p
                style={{
                    textDecoration: todo.completed ? 'line-through' : null,
                }}
            >
                {todo.task}
            </p>
            <button onClick={() => handleDelete(todo.id)}>X</button>
        </div>
    )
}

export default Todo
