import React from 'react'

const Todo = ({ todo, handleDelete, resolved }) => {
    const toogle = (id) => {
        resolved(todo.id)
    }
    return (
        <div
            style={{
                display: 'flex',
            }}
        >
            <p
                style={{
                    textDecoration: todo.completed ? 'line-through' : null,
                }}
            >
                {todo.task}
            </p>
            <button onClick={() => handleDelete(todo.id)}>X</button>
        </div>
    )
}

export default Todo
