import styled from 'styled-components'

export const Container = styled.div`
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    height: 300px;
    width: 300px;
    border: 1px solid black;
    margin: 10px 10px;
    background-color: rgb(0, 0, 0, 0.2);
`
export const Image = styled.img`
    height: 100%;
    width: 100%;
`
export const Icon = styled.div``
