import React from 'react'

function Todo({ todo, removeTodo, todoComplete }) {
    return (
        <div
            style={{
                display: 'flex',
                alignItems: 'center',
            }}
        >
            <button onClick={() => removeTodo(todo.id)}>X</button>
            <p
                style={{
                    textDecoration: todo.complete ? 'line-through' : null,
                }}
            >
                {todo.task}
            </p>
            <input onClick={() => todoComplete(todo.id)} type="checkbox" />
        </div>
    )
}

export default Todo
